                                    var min = parseInt($("input[name='product_qty']").attr("min"), 10);
                                    if(value > max)
                                    {
                                        value = max;
                                        $("input[name='product_qty']").val(value);
                                    }
                                    else if(value < min)
                                    {
                                        value = min;
                                        $("input[name='product_qty']").val(value);
                                    }
                                }
                                $("input[name='product_qty']").keyup(function()
                                {
                                    limitquantity();

                                });

                            });
